CREATE TABLE MILES (ID DECIMAL(4) NOT NULL, MILE_TYPE VARCHAR(30) DEFAULT 'Walking'  NOT NULL, MILE_DATE DATE NOT NULL, START_TIME TIME NOT NULL, END_TIME TIME NOT NULL, DISTANCE DECIMAL(6) DEFAULT 0  NOT NULL, TITLE VARCHAR(50) DEFAULT 'Walked Home'  NOT NULL, LOCATION VARCHAR(100), DESCRIPTION VARCHAR(200), PRIMARY KEY (ID));


CREATE TABLE USERS (USER_ID DECIMAL(4) NOT NULL, FIRST_NAME VARCHAR(30) NOT NULL, MIDDLE_NAME VARCHAR(30), LAST_NAME VARCHAR(30), EMAIL VARCHAR(40) NOT NULL, AGE DECIMAL(3), GENDER CHAR(1), USERNAME VARCHAR(20) NOT NULL, PASSWORD VARCHAR(30) DEFAULT 'passwordpasswordpassword'  NOT NULL, PRIMARY KEY (USER_ID));
